import React, { useState, useEffect } from 'react';
import { 
  TrendingUp, 
  TrendingDown, 
  DollarSign, 
  PieChart,
  ArrowUpRight,
  ArrowDownRight,
  Calendar,
  CheckCircle,
  XCircle,
  BarChart3,
  Target,
  AlertCircle
} from 'lucide-react';
import { 
  AreaChart, 
  Area, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  ResponsiveContainer,
  PieChart as RechartsPieChart,
  Pie,
  Cell
} from 'recharts';
import { expensesAPI, incomesAPI, categoriesAPI, dashboardAPI, analyticsAPI, formatCurrency, formatDate, formatPercentage as formatPercentageUtil } from '../services/api';
import { usePeriod } from '../contexts/PeriodContext';
import { useAuth } from '../contexts/AuthContext';
import toast from 'react-hot-toast';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [sortBy, setSortBy] = useState('fecha'); // nuevo estado para ordenamiento
  const [data, setData] = useState({
    totalIncome: 0,
    totalExpenses: 0,
    balance: 0,
    expenses: [],
    incomes: [],
    categories: [],
  });

  // Usar el contexto global de perÃ­odo
  const {
    selectedYear,
    selectedMonth,
    hasActiveFilters,
    balancesHidden,
    getFilterParams,
    getPeriodTitle,
    updateAvailableData,
  } = usePeriod();

  // Usar el contexto de autenticaciÃ³n
  const { user, isAuthenticated, getAuthHeaders } = useAuth();

  // Debug de autenticaciÃ³n
  useEffect(() => {
    console.log('ðŸ” Dashboard - Estado de autenticaciÃ³n:', {
      isAuthenticated,
      user,
      token: localStorage.getItem('auth_token') ? 'EXISTS' : 'MISSING',
      authHeaders: getAuthHeaders()
    });
  }, [isAuthenticated, user, getAuthHeaders]);

  // Cargar datos iniciales (incluyendo calcular meses disponibles)
  useEffect(() => {
    loadDashboardData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Recargar datos cuando cambien los filtros del contexto global (sin recalcular meses)
  useEffect(() => {
    if (selectedMonth !== null || selectedYear !== null) {
      loadDashboardData(false); // false = no recalcular meses disponibles
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [selectedMonth, selectedYear]);

  const loadDashboardData = async (shouldUpdateAvailableData = true) => {
    try {
      setLoading(true);

      // Obtener parÃ¡metros de filtro del contexto global
      const filterParams = getFilterParams();

      let data = {};

      // Solo usar nuevos endpoints si el usuario estÃ¡ autenticado
      if (isAuthenticated && user) {
        try {
          console.log('ðŸš€ Cargando datos con nuevos endpoints optimizados...');

