      // Validar que la fecha sea vÃ¡lida
      if (isNaN(itemDate.getTime())) return false;

      // Filtrar por aÃ±o si estÃ¡ seleccionado
      if (yearFilter && itemDate.getFullYear().toString() !== yearFilter) {
        return false;
      }

      // Filtrar por mes si estÃ¡ seleccionado
      if (monthFilter) {
        const itemMonth = itemDate.toISOString().slice(0, 7);
        return itemMonth === monthFilter;
