import React, { createContext, useContext, useState } from 'react';

// Crear el contexto con valores por defecto
const AuthContext = createContext({
  isAuthenticated: false,
  user: null,
  login: async () => {},
  logout: async () => {},
  register: async () => {},
});

// Hook personalizado para usar el contexto
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    console.error('âŒ useAuth llamado fuera del AuthProvider');
    throw new Error('useAuth debe ser usado dentro de un AuthProvider');
  }
  console.log('âœ… useAuth hook funcionando:', context);
  return context;
};

/**
 * Proveedor del contexto de autenticaciÃ³n - VERSION ULTRA SIMPLE
 */
export const AuthProvider = ({ children }) => {
  console.log('ðŸ”§ AuthProvider iniciando...');

  // Estado simple
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Funciones bÃ¡sicas
  const login = async (credentials) => {
    console.log('ðŸ”§ Login simulado:', credentials);
    const mockUser = { 
      id: 1, 
      email: credentials.email, 
      first_name: 'Usuario', 
      last_name: 'Test' 
    };
    setUser(mockUser);
    setIsAuthenticated(true);
    return { success: true, data: { user: mockUser } };
  };

  const register = async (userData) => {
    console.log('ðŸ”§ Register simulado:', userData);
    const mockUser = { 
      id: 1, 
      email: userData.email, 
      first_name: userData.firstName || 'Usuario', 
      last_name: userData.lastName || 'Test' 
    };
    setUser(mockUser);
    setIsAuthenticated(true);
    return { success: true, data: { user: mockUser } };
  };

  const logout = async () => {
    console.log('ðŸ”§ Logout simulado');
    setUser(null);
    setIsAuthenticated(false);
  };

  // Valor del contexto
  const contextValue = {
    // Estado
    user,
    isAuthenticated,
    isLoading: false,
    isInitialized: true,

    // Acciones
    login,
    register,
    logout,

    // Funciones dummy
    updateProfile: async () => ({ success: true }),
    changePassword: async () => ({ success: true }),
    refreshToken: async () => ({ success: true }),
    hasRole: () => false,
    getAuthHeaders: () => ({}),
  };

  console.log('ðŸ”§ AuthProvider valor del contexto:', {
    isAuthenticated,
    userEmail: user?.email || 'no user'
  });

  return (
    <AuthContext.Provider value={contextValue}>
      {children}
    </AuthContext.Provider>
  );
};

/**
 * Hook para verificar autenticaciÃ³n
 */
export const useAuthStatus = () => {
  const { isAuthenticated, isLoading, isInitialized } = useAuth();

  return {
    isAuthenticated,
    isLoading: isLoading || !isInitialized,
    isReady: isInitialized && !isLoading,
  };
};

/**
 * Hook para obtener datos del usuario
 */
export const useUser = () => {
  const { user } = useAuth();

  const fullName = user 
    ? `${user.first_name || ''} ${user.last_name || ''}`.trim() || 'Usuario'
    : 'Usuario';

  const initials = user 
    ? `${user.first_name?.[0] || ''}${user.last_name?.[0] || ''}` 
    : 'U';

  return {
    user,
    fullName,
    initials,
    updateProfile: async () => ({ success: true }),
  };
};

/**
 * Hook para acciones de autenticaciÃ³n
 */
export const useAuthActions = () => {
  const { login, register, logout, changePassword } = useAuth();

  return {
    login,
    register,
    logout,
    changePassword,
  };
};

export default AuthContext;
