import React, { useState, useEffect } from 'react';
import { 
  TrendingUp, 
  TrendingDown, 
  DollarSign, 
  PieChart,
  ArrowUpRight,
  ArrowDownRight,
  Calendar,
  Eye,
  EyeOff,
  Filter
} from 'lucide-react';
import { 
  AreaChart, 
  Area, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  ResponsiveContainer,
  PieChart as RechartsPieChart,
  Pie,
  Cell
} from 'recharts';
import { expensesAPI, incomesAPI, categoriesAPI, formatCurrency, formatPercentage } from '../services/api';
import toast from 'react-hot-toast';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [showAmounts, setShowAmounts] = useState(true);
  const [selectedMonth, setSelectedMonth] = useState(new Date().toISOString().slice(0, 7)); // YYYY-MM format
  const [data, setData] = useState({
    totalIncome: 0,
    totalExpenses: 0,
    balance: 0,
    expenses: [],
    incomes: [],
    categories: [],
  });

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);

      // TODO: Remover datos mock cuando el backend est√© funcionando
      const MOCK_DATA = {
        expenses: [
          {
            id: 1,
            user_id: 'user123',
            description: 'Supermercado',
            amount: 15000,
            category_id: 'cat1',
            paid: true,
            due_date: '2024-12-15',
            created_at: '2024-12-10T10:00:00Z',
            amount_paid: 15000
          },
          {
            id: 2,
            user_id: 'user123',
            description: 'Netflix',
            amount: 2500,
            category_id: 'cat2',
            paid: false,
            due_date: '2024-12-20',
            created_at: '2024-12-08T14:30:00Z',
            amount_paid: 0
          },
          {
            id: 3,
            user_id: 'user123',
            description: 'Combustible',
            amount: 8000,
            category_id: 'cat3',
            paid: true,
            due_date: null,
            created_at: '2024-11-25T09:15:00Z',
            amount_paid: 8000
          },
          {
            id: 4,
            user_id: 'user123',
            description: 'Farmacia',
            amount: 4500,
            category_id: 'cat4',
            paid: false,
            due_date: '2024-12-25',
            created_at: '2024-12-12T16:45:00Z',
            amount_paid: 0
          }
        ],
        incomes: [
          {
            id: 1,
            user_id: 'user123',
            description: 'Sueldo',
            amount: 180000,
            created_at: '2024-12-01T08:00:00Z',
            percentage: 85.5
          },
          {
            id: 2,
            user_id: 'user123',
            description: 'Freelance',
            amount: 25000,
            created_at: '2024-12-05T15:30:00Z',
            percentage: 14.5
          },
          {
            id: 3,
            user_id: 'user123',
            description: 'Venta online',
            amount: 12000,
            created_at: '2024-11-28T12:00:00Z',
            percentage: 8.2
          }
        ],
        categories: [
          { id: 'cat1', name: 'Alimentaci√≥n', description: 'Supermercado y comida' },
          { id: 'cat2', name: 'Entretenimiento', description: 'Streaming y ocio' },
          { id: 'cat3', name: 'Transporte', description: 'Combustible y transporte' },
          { id: 'cat4', name: 'Salud', description: 'Medicamentos y salud' }
        ]
      };

      // Usar datos mock si las APIs fallan
      let expenses = [];
      let incomes = [];
      let categories = [];

      try {
        const [expensesResponse, incomesResponse, categoriesResponse] = await Promise.all([
          expensesAPI.list(),
          incomesAPI.list(),
          categoriesAPI.list(),
        ]);

        // Asegurar que siempre sean arrays
        const expensesData = expensesResponse.data?.expenses || expensesResponse.data || [];
        const incomesData = incomesResponse.data?.incomes || incomesResponse.data || [];
        const categoriesData = categoriesResponse.data?.data || categoriesResponse.data || [];

        expenses = Array.isArray(expensesData) ? expensesData : [];
        incomes = Array.isArray(incomesData) ? incomesData : [];
        categories = Array.isArray(categoriesData) ? categoriesData : [];
      } catch (backendError) {
        console.warn('Backend no disponible, usando datos mock:', backendError.message);
        // Usar datos mock si el backend no est√° disponible
        expenses = MOCK_DATA.expenses;
        incomes = MOCK_DATA.incomes;
        categories = MOCK_DATA.categories;

        toast.success('üìù Usando datos de ejemplo (Backend no conectado)', {
          duration: 3000,
          style: {
            background: '#FFF3CD',
            color: '#856404',
            border: '1px solid #FFEAA7'
          }
        });
      }

      const totalExpenses = expenses.reduce((sum, expense) => sum + expense.amount, 0);
      const totalIncome = incomes.reduce((sum, income) => sum + income.amount, 0);

      setData({
        totalIncome,
        totalExpenses,
        balance: totalIncome - totalExpenses,
        expenses,
        incomes,
        categories,
      });
    } catch (error) {
      console.error('Error loading dashboard data:', error);
      toast.error('Error al cargar los datos del dashboard');
      setData({
        totalIncome: 0,
        totalExpenses: 0,
        balance: 0,
        expenses: [],
        incomes: [],
        categories: [],
      });
    } finally {
      setLoading(false);
    }
  };

  const formatAmount = (amount) => {
    if (!showAmounts) return '‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢';
    return formatCurrency(amount);
  };

  // Funci√≥n para filtrar datos por mes seleccionado
  const filterDataByMonth = (dataArray, monthFilter) => {
    if (!monthFilter) return dataArray;
    return dataArray.filter(item => {
      const itemDate = new Date(item.created_at);
      const itemMonth = itemDate.toISOString().slice(0, 7);
      return itemMonth === monthFilter;
    });
  };

  // Datos filtrados por mes (calcular despu√©s de cargar datos)
  const filteredExpenses = filterDataByMonth(data.expenses, selectedMonth);
  const filteredIncomes = filterDataByMonth(data.incomes, selectedMonth);
  const monthlyTotalExpenses = filteredExpenses.reduce((sum, expense) => sum + expense.amount, 0);
  const monthlyTotalIncome = filteredIncomes.reduce((sum, income) => sum + income.amount, 0);
  const monthlyBalance = monthlyTotalIncome - monthlyTotalExpenses;

  // Generar opciones de meses basadas en los datos disponibles
  const getAvailableMonths = () => {
    const months = new Set();

    // Agregar meses de gastos e ingresos
    [...data.expenses, ...data.incomes].forEach(item => {
      if (item.created_at) {
        const month = new Date(item.created_at).toISOString().slice(0, 7);
        months.add(month);
      }
    });

    // Siempre incluir el mes actual
    const currentMonth = new Date().toISOString().slice(0, 7);
    months.add(currentMonth);

    // Convertir a array y ordenar
    return Array.from(months).sort().reverse();
  };

  const formatMonthLabel = (monthString) => {
    const date = new Date(monthString + '-01');
    return date.toLocaleDateString('es-AR', { 
      year: 'numeric', 
      month: 'long' 
    });
  };

  // Funci√≥n para calcular datos reales del gr√°fico de torta por categor√≠as
  const calculateCategoryData = () => {
    // Usar datos filtrados si hay un mes seleccionado, sino usar todos los datos
    const expensesToUse = selectedMonth ? filteredExpenses : data.expenses;
    const totalExpensesToUse = selectedMonth ? monthlyTotalExpenses : data.totalExpenses;

    if (!expensesToUse.length || !data.categories.length) {
      return [];
    }

    // Agrupar gastos por categor√≠a
    const categoryTotals = {};
    expensesToUse.forEach(expense => {
      const categoryId = expense.category_id || 'sin_categoria';
      if (!categoryTotals[categoryId]) {
        categoryTotals[categoryId] = 0;
      }
      categoryTotals[categoryId] += expense.amount;
    });

    // Convertir a array con nombres y porcentajes
    const colors = ['#009ee3', '#00a650', '#ff6900', '#e53e3e', '#6b7280', '#8b5cf6', '#f59e0b'];
    let colorIndex = 0;

    return Object.entries(categoryTotals).map(([categoryId, amount]) => {
      const category = data.categories.find(c => c.id === categoryId);
      const name = category ? category.name : 'Sin categor√≠a';
      const percentage = totalExpensesToUse > 0 ? Math.round((amount / totalExpensesToUse) * 100) : 0;

      return {
        name,
        value: percentage,
        amount,
        color: colors[colorIndex++ % colors.length]
      };
    }).filter(item => item.value > 0); // Solo mostrar categor√≠as con gastos
  };

  // Funci√≥n para calcular datos del gr√°fico de √°rea
  const calculateChartData = () => {
    if (selectedMonth) {
      // Si hay un mes seleccionado, mostrar datos de ese mes espec√≠fico
      const monthLabel = new Date(selectedMonth + '-01').toLocaleDateString('es-AR', { month: 'short' });
      return [{
        name: monthLabel,
        ingresos: monthlyTotalIncome,
        gastos: monthlyTotalExpenses
      }];
    } else {
      // L√≥gica original para mostrar tendencia general
      const currentMonth = new Date().toLocaleDateString('es-AR', { month: 'short' });
      const months = ['Ene', 'Feb', 'Mar', 'Abr', 'May', 'Jun', 'Jul', 'Ago', 'Sep', 'Oct', 'Nov', 'Dic'];
      const currentMonthIndex = new Date().getMonth();

      const chartData = [];

      // Agregar 2-3 meses anteriores en 0
      for (let i = Math.max(0, currentMonthIndex - 2); i < currentMonthIndex; i++) {
        chartData.push({
          name: months[i],
          ingresos: 0,
          gastos: 0
        });
      }

      // Agregar el mes actual con datos reales
      chartData.push({
        name: currentMonth,
        ingresos: data.totalIncome,
        gastos: data.totalExpenses
      });

      return chartData;
    }
  };

  // Datos para los gr√°ficos
  const chartData = calculateChartData();
  const pieData = calculateCategoryData();

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="spinner"></div>
        <span className="ml-2 text-mp-gray-600">Cargando dashboard...</span>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header con toggle de visibilidad y selector de mes */}
      <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between space-y-4 lg:space-y-0">
        <div>
          <h1 className="text-2xl font-bold text-mp-gray-900">Dashboard</h1>
          <p className="text-mp-gray-600">Resumen de tu actividad financiera</p>
        </div>

        <div className="flex flex-col sm:flex-row items-start sm:items-center space-y-4 sm:space-y-0 sm:space-x-4">
          {/* Selector de mes */}
          <div className="relative">
            <label className="block text-sm font-medium text-mp-gray-700 mb-2">
              Filtrar por mes
            </label>
            <div className="relative">
              <select
                value={selectedMonth}
                onChange={(e) => setSelectedMonth(e.target.value)}
                className="input pr-10 w-full sm:w-auto min-w-[200px]"
              >
                <option value="">Todos los meses</option>
                {getAvailableMonths().map(month => (
                  <option key={month} value={month}>
                    {formatMonthLabel(month)}
                  </option>
                ))}
              </select>
              <Filter className="absolute right-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-mp-gray-400 pointer-events-none" />
            </div>
          </div>

          {/* Toggle de visibilidad */}
          <div className="self-end">
            <button
              onClick={() => setShowAmounts(!showAmounts)}
              className="btn-ghost flex items-center space-x-2"
            >
              {showAmounts ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
              <span>{showAmounts ? 'Ocultar' : 'Mostrar'} montos</span>
            </button>
          </div>
        </div>
      </div>

      {/* M√©tricas del mes seleccionado */}
      {selectedMonth && (
        <div className="bg-gradient-to-r from-mp-primary to-mp-primary-dark rounded-mp-lg p-6 text-white">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-xl font-bold">
              üìÖ {formatMonthLabel(selectedMonth)}
            </h3>
            <div className="text-right">
              <p className="text-sm opacity-90">Balance del mes</p>
              <p className={`text-2xl font-bold ${monthlyBalance >= 0 ? 'text-green-300' : 'text-red-300'}`}>
                {formatAmount(monthlyBalance)}
              </p>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Ingresos del mes */}
            <div className="bg-white bg-opacity-10 rounded-mp p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm opacity-90">Ingresos</p>
                  <p className="text-xl font-bold text-green-300">
                    {formatAmount(monthlyTotalIncome)}
                  </p>
                  <p className="text-xs opacity-75 mt-1">
                    {filteredIncomes.length} {filteredIncomes.length === 1 ? 'ingreso' : 'ingresos'}
                  </p>
                </div>
                <TrendingUp className="w-8 h-8 text-green-300" />
              </div>
            </div>

            {/* Gastos del mes */}
            <div className="bg-white bg-opacity-10 rounded-mp p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm opacity-90">Gastos</p>
                  <p className="text-xl font-bold text-orange-300">
                    {formatAmount(monthlyTotalExpenses)}
                  </p>
                  <p className="text-xs opacity-75 mt-1">
                    {filteredExpenses.length} {filteredExpenses.length === 1 ? 'gasto' : 'gastos'}
                  </p>
                </div>
                <TrendingDown className="w-8 h-8 text-orange-300" />
              </div>
            </div>
          </div>
        </div>
      )}

      {/* M√©tricas principales */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {/* Balance total */}
        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-mp-gray-600">Balance Total</p>
              <p className={`text-2xl font-bold ${data.balance >= 0 ? 'text-mp-secondary' : 'text-mp-error'}`}>
                {formatAmount(data.balance)}
              </p>
            </div>
            <div className={`p-3 rounded-mp ${data.balance >= 0 ? 'bg-green-100' : 'bg-red-100'}`}>
              <DollarSign className={`w-6 h-6 ${data.balance >= 0 ? 'text-mp-secondary' : 'text-mp-error'}`} />
            </div>
          </div>
          <div className="mt-4 flex items-center">
            {data.balance >= 0 ? (
              <ArrowUpRight className="w-4 h-4 text-mp-secondary mr-1" />
            ) : (
              <ArrowDownRight className="w-4 h-4 text-mp-error mr-1" />
            )}
            <span className={`text-sm ${data.balance >= 0 ? 'text-mp-secondary' : 'text-mp-error'}`}>
              {data.balance >= 0 ? 'Positivo' : 'Negativo'}
            </span>
          </div>
        </div>

        {/* Total ingresos */}
        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-mp-gray-600">Total Ingresos</p>
              <p className="text-2xl font-bold text-mp-secondary">
                {formatAmount(data.totalIncome)}
              </p>
            </div>
            <div className="p-3 rounded-mp bg-green-100">
              <TrendingUp className="w-6 h-6 text-mp-secondary" />
            </div>
          </div>
          <div className="mt-4 flex items-center">
            <TrendingUp className="w-4 h-4 text-mp-secondary mr-1" />
            <span className="text-sm text-mp-gray-500">
              {data.incomes.length} {data.incomes.length === 1 ? 'ingreso registrado' : 'ingresos registrados'}
            </span>
          </div>
        </div>

        {/* Total gastos */}
        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-mp-gray-600">Total Gastos</p>
              <p className="text-2xl font-bold text-mp-accent">
                {formatAmount(data.totalExpenses)}
              </p>
            </div>
            <div className="p-3 rounded-mp bg-orange-100">
              <TrendingDown className="w-6 h-6 text-mp-accent" />
            </div>
          </div>
          <div className="mt-4 flex items-center">
            <TrendingDown className="w-4 h-4 text-mp-accent mr-1" />
            <span className="text-sm text-mp-gray-500">
              {data.expenses.length} {data.expenses.length === 1 ? 'gasto registrado' : 'gastos registrados'}
            </span>
          </div>
        </div>

        {/* Gastos pendientes */}
        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-mp-gray-600">Gastos Pendientes</p>
              <p className="text-2xl font-bold text-mp-error">
                {data.expenses.filter(e => !e.paid).length}
              </p>
            </div>
            <div className="p-3 rounded-mp bg-red-100">
              <Calendar className="w-6 h-6 text-mp-error" />
            </div>
          </div>
          <div className="mt-4">
            <span className="text-sm text-mp-gray-500">
              {formatAmount(data.expenses.filter(e => !e.paid).reduce((sum, e) => sum + e.amount, 0))} por pagar
            </span>
          </div>
        </div>
      </div>

      {/* Transacciones por mes - Dos columnas */}
      {selectedMonth && (filteredExpenses.length > 0 || filteredIncomes.length > 0) && (
        <div className="card">
          <div className="flex items-center justify-between mb-6">
            <h3 className="text-lg font-semibold text-mp-gray-900">
              üí∞ Transacciones de {formatMonthLabel(selectedMonth)}
            </h3>
            <div className="flex items-center space-x-4 text-sm">
              <div className="flex items-center">
                <div className="w-3 h-3 bg-mp-error rounded-full mr-2"></div>
                <span className="text-mp-gray-600">Gastos ({filteredExpenses.length})</span>
              </div>
              <div className="flex items-center">
                <div className="w-3 h-3 bg-mp-secondary rounded-full mr-2"></div>
                <span className="text-mp-gray-600">Ingresos ({filteredIncomes.length})</span>
              </div>
            </div>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Columna de Gastos */}
            <div className="space-y-4">
              <div className="flex items-center justify-between mb-4">
                <h4 className="font-semibold text-mp-error flex items-center">
                  <TrendingDown className="w-5 h-5 mr-2" />
                  Gastos
                </h4>
                <span className="text-lg font-bold text-mp-error">
                  {formatAmount(monthlyTotalExpenses)}
                </span>
              </div>

              <div className="space-y-3 max-h-96 overflow-y-auto">
                {filteredExpenses.length === 0 ? (
                  <div className="text-center py-8 text-mp-gray-500">
                    <TrendingDown className="w-8 h-8 mx-auto mb-2 opacity-50" />
                    <p>No hay gastos en este mes</p>
                  </div>
                ) : (
                  filteredExpenses
                    .sort((a, b) => new Date(b.created_at) - new Date(a.created_at))
                    .map((expense, index) => {
                      const category = data.categories.find(c => c.id === expense.category_id);
                      return (
                        <div key={expense.id || index} className="flex items-center justify-between p-3 rounded-mp bg-red-50 border-l-4 border-mp-error">
                          <div className="flex-1">
                            <div className="flex items-center space-x-2">
                              <p className="font-medium text-mp-gray-900 text-sm">
                                {expense.description}
                              </p>
                              {!expense.paid && (
                                <span className="badge-error text-xs">Pendiente</span>
                              )}
                              {category && (
                                <span className="badge-info text-xs">{category.name}</span>
                              )}
                            </div>
                            <p className="text-xs text-mp-gray-500 mt-1">
                              {new Date(expense.created_at).toLocaleDateString('es-AR')}
                              {expense.due_date && (
                                <span className="ml-2">
                                  ‚Ä¢ Vence: {new Date(expense.due_date).toLocaleDateString('es-AR')}
                                </span>
                              )}
                            </p>
                          </div>
                          <div className="text-right ml-3">
                            <p className="font-semibold text-mp-error">
                              -{formatAmount(expense.amount)}
                            </p>
                            {expense.amount_paid > 0 && expense.amount_paid < expense.amount && (
                              <p className="text-xs text-mp-gray-500">
                                Pagado: {formatAmount(expense.amount_paid)}
                              </p>
                            )}
                          </div>
                        </div>
                      );
                    })
                )}
              </div>
            </div>

            {/* Columna de Ingresos */}
            <div className="space-y-4">
              <div className="flex items-center justify-between mb-4">
                <h4 className="font-semibold text-mp-secondary flex items-center">
                  <TrendingUp className="w-5 h-5 mr-2" />
                  Ingresos
                </h4>
                <span className="text-lg font-bold text-mp-secondary">
                  {formatAmount(monthlyTotalIncome)}
                </span>
              </div>

              <div className="space-y-3 max-h-96 overflow-y-auto">
                {filteredIncomes.length === 0 ? (
                  <div className="text-center py-8 text-mp-gray-500">
                    <TrendingUp className="w-8 h-8 mx-auto mb-2 opacity-50" />
                    <p>No hay ingresos en este mes</p>
                  </div>
                ) : (
                  filteredIncomes
                    .sort((a, b) => new Date(b.created_at) - new Date(a.created_at))
                    .map((income, index) => (
                      <div key={income.id || index} className="flex items-center justify-between p-3 rounded-mp bg-green-50 border-l-4 border-mp-secondary">
                        <div className="flex-1">
                          <p className="font-medium text-mp-gray-900 text-sm">
                            {income.description}
                          </p>
                          <p className="text-xs text-mp-gray-500 mt-1">
                            {new Date(income.created_at).toLocaleDateString('es-AR')}
                          </p>
                        </div>
                        <div className="text-right ml-3">
                          <p className="font-semibold text-mp-secondary">
                            +{formatAmount(income.amount)}
                          </p>
                          {income.percentage && (
                            <p className="text-xs text-mp-gray-500">
                              {formatPercentage(income.percentage)}
                            </p>
                          )}
                        </div>
                      </div>
                    ))
                )}
              </div>
            </div>
          </div>

          {/* Resumen del balance del mes */}
          <div className="mt-6 pt-4 border-t border-mp-gray-200">
            <div className="flex items-center justify-between">
              <span className="text-mp-gray-600">Balance del mes:</span>
              <span className={`text-xl font-bold ${monthlyBalance >= 0 ? 'text-mp-secondary' : 'text-mp-error'}`}>
                {monthlyBalance >= 0 ? '+' : ''}{formatAmount(monthlyBalance)}
              </span>
            </div>
          </div>
        </div>
      )}

      {/* Gr√°ficos */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Gr√°fico de tendencias */}
        <div className="card">
          <div className="flex items-center justify-between mb-6">
            <div>
              <h3 className="text-lg font-semibold text-mp-gray-900">
                {selectedMonth ? `Datos de ${formatMonthLabel(selectedMonth)}` : 'Tendencia Mensual'}
              </h3>
              <p className="text-sm text-mp-gray-500 mt-1">
                {selectedMonth 
                  ? 'Resumen del mes seleccionado' 
                  : 'Datos del mes actual (hist√≥rico pr√≥ximamente)'
                }
              </p>
            </div>
            <div className="flex items-center space-x-4 text-sm">
              <div className="flex items-center">
                <div className="w-3 h-3 bg-mp-secondary rounded-full mr-2"></div>
                <span className="text-mp-gray-600">Ingresos</span>
              </div>
              <div className="flex items-center">
                <div className="w-3 h-3 bg-mp-accent rounded-full mr-2"></div>
                <span className="text-mp-gray-600">Gastos</span>
              </div>
            </div>
          </div>
          <ResponsiveContainer width="100%" height={300}>
            <AreaChart data={chartData}>
              <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
              <XAxis dataKey="name" stroke="#6b7280" />
              <YAxis stroke="#6b7280" />
              <Tooltip 
                formatter={(value) => [formatCurrency(value)]}
                contentStyle={{
                  backgroundColor: 'white',
                  border: '1px solid #e5e7eb',
                  borderRadius: '8px',
                  boxShadow: '0 4px 12px 0 rgba(0, 0, 0, 0.15)',
                }}
              />
              <Area
                type="monotone"
                dataKey="ingresos"
                stackId="1"
                stroke="#00a650"
                fill="#00a650"
                fillOpacity={0.6}
              />
              <Area
                type="monotone"
                dataKey="gastos"
                stackId="2"
                stroke="#ff6900"
                fill="#ff6900"
                fillOpacity={0.6}
              />
            </AreaChart>
          </ResponsiveContainer>
        </div>

        {/* Gr√°fico de categor√≠as */}
        <div className="card">
          <h3 className="text-lg font-semibold text-mp-gray-900 mb-6">
            Gastos por Categor√≠a{selectedMonth && ` - ${formatMonthLabel(selectedMonth)}`}
          </h3>
          {pieData.length > 0 ? (
            <>
              <div className="flex items-center justify-center">
                <ResponsiveContainer width="100%" height={300}>
                  <RechartsPieChart>
                    <Pie
                      data={pieData}
                      cx="50%"
                      cy="50%"
                      innerRadius={60}
                      outerRadius={120}
                      paddingAngle={5}
                      dataKey="value"
                    >
                      {pieData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={entry.color} />
                      ))}
                    </Pie>
                    <Tooltip 
                      formatter={(value, name, props) => [
                        `${value}% (${formatCurrency(props.payload.amount)})`, 
                        'Porcentaje'
                      ]}
                      contentStyle={{
                        backgroundColor: 'white',
                        border: '1px solid #e5e7eb',
                        borderRadius: '8px',
                        boxShadow: '0 4px 12px 0 rgba(0, 0, 0, 0.15)',
                      }}
                    />
                  </RechartsPieChart>
                </ResponsiveContainer>
              </div>
              <div className="mt-4 space-y-2">
                {pieData.map((item, index) => (
                  <div key={index} className="flex items-center justify-between">
                    <div className="flex items-center">
                      <div 
                        className="w-3 h-3 rounded-full mr-2" 
                        style={{ backgroundColor: item.color }}
                      ></div>
                      <span className="text-sm text-mp-gray-600">{item.name}</span>
                    </div>
                    <div className="text-right">
                      <span className="text-sm font-medium text-mp-gray-900">{item.value}%</span>
                      <div className="text-xs text-mp-gray-500">{formatCurrency(item.amount)}</div>
                    </div>
                  </div>
                ))}
              </div>
            </>
          ) : (
            <div className="flex flex-col items-center justify-center h-64 text-center">
              <PieChart className="w-12 h-12 text-mp-gray-400 mb-4" />
              <h4 className="text-lg font-medium text-mp-gray-600 mb-2">No hay gastos por categor√≠as</h4>
              <p className="text-sm text-mp-gray-500">
                Los gastos aparecer√°n aqu√≠ una vez que agregues algunos gastos con categor√≠as
              </p>
            </div>
          )}
        </div>
      </div>

      {/* Transacciones recientes */}
      <div className="card">
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-lg font-semibold text-mp-gray-900">Transacciones Recientes</h3>
          <button className="btn-ghost">Ver todas</button>
        </div>
        <div className="space-y-4">
          {[...data.expenses.slice(0, 3), ...data.incomes.slice(0, 2)]
            .sort((a, b) => new Date(b.created_at) - new Date(a.created_at))
            .slice(0, 5)
            .map((transaction, index) => {
              const isExpense = transaction.hasOwnProperty('paid');
              return (
                <div key={index} className="flex items-center justify-between p-4 rounded-mp bg-mp-gray-50">
                  <div className="flex items-center space-x-3">
                    <div className={`p-2 rounded-mp ${isExpense ? 'bg-red-100' : 'bg-green-100'}`}>
                      {isExpense ? (
                        <TrendingDown className="w-4 h-4 text-mp-error" />
                      ) : (
                        <TrendingUp className="w-4 h-4 text-mp-secondary" />
                      )}
                    </div>
                    <div>
                      <p className="font-medium text-mp-gray-900">{transaction.description}</p>
                      <p className="text-sm text-mp-gray-500">
                        {new Date(transaction.created_at).toLocaleDateString('es-AR')}
                      </p>
                    </div>
                  </div>
                  <div className="text-right">
                    <p className={`font-semibold ${isExpense ? 'text-mp-error' : 'text-mp-secondary'}`}>
                      {isExpense ? '-' : '+'}{formatAmount(transaction.amount)}
                    </p>
                    {transaction.percentage && (
                      <p className="text-sm text-mp-gray-500">
                        {formatPercentage(transaction.percentage)}
                      </p>
                    )}
                  </div>
                </div>
              );
            })}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
