import React, { createContext, useContext, useEffect, useState, useCallback } from 'react';
import authService from '../services/authService';

// Crear el contexto
const AuthContext = createContext();

// Hook personalizado para usar el contexto
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth debe ser usado dentro de un AuthProvider');
  }
  return context;
};

// Estados posibles de autenticaci贸n
export const AUTH_STATES = {
  LOADING: 'loading',
  AUTHENTICATED: 'authenticated',
  UNAUTHENTICATED: 'unauthenticated',
  ERROR: 'error',
};

/**
 * Proveedor del contexto de autenticaci贸n - VERSION SIMPLIFICADA PARA DEBUG
 */
export const AuthProvider = ({ children }) => {
  console.log(' AuthProvider rendering...');

  const [authState, setAuthState] = useState(AUTH_STATES.UNAUTHENTICATED);
  const [user, setUser] = useState(null);
  const [isInitialized, setIsInitialized] = useState(true);

  // Funciones b谩sicas para evitar errores
  const handleLogin = useCallback(async (credentials) => {
    try {
      setAuthState(AUTH_STATES.LOADING);
      const result = await authService.login(credentials);
      setUser(result.data.user);
      setAuthState(AUTH_STATES.AUTHENTICATED);
      return result;
    } catch (error) {
      setAuthState(AUTH_STATES.UNAUTHENTICATED);
      throw error;
    }
  }, []);

  const handleRegister = useCallback(async (userData) => {
    try {
      setAuthState(AUTH_STATES.LOADING);
      const result = await authService.register(userData);
      setUser(result.data.user);
      setAuthState(AUTH_STATES.AUTHENTICATED);
      return result;
    } catch (error) {
      setAuthState(AUTH_STATES.UNAUTHENTICATED);
      throw error;
    }
  }, []);

  const handleLogout = useCallback(async () => {
    try {
      await authService.logout();
    } catch (error) {
      console.warn('Error during logout:', error);
    } finally {
      setUser(null);
      setAuthState(AUTH_STATES.UNAUTHENTICATED);
    }
  }, []);

  // Valores del contexto simplificados
  const contextValue = {
    // Estado
    authState,
    user,
    isInitialized,

    // Estados derivados
    isAuthenticated: authState === AUTH_STATES.AUTHENTICATED,
    isLoading: authState === AUTH_STATES.LOADING,
    isError: authState === AUTH_STATES.ERROR,

    // Acciones b谩sicas
    register: handleRegister,
    login: handleLogin,
    logout: handleLogout,

    // Funciones dummy para evitar errores
    updateProfile: async () => ({ success: true }),
    changePassword: async () => ({ success: true }),
    refreshToken: async () => ({ success: true }),
    hasRole: () => false,
    getAuthHeaders: () => ({}),
    timeUntilExpiry: 0,
    expiresAt: null,
  };

  console.log(' AuthProvider context value:', { 
    authState, 
    isAuthenticated: contextValue.isAuthenticated, 
    user: user?.email || 'no user' 
  });

  return (
    <AuthContext.Provider value={contextValue}>
      {console.log(' AuthProvider rendering children...')}
      {children}
    </AuthContext.Provider>
  );
};

/**
 * Hook para verificar autenticaci贸n con loading
 */
export const useAuthStatus = () => {
  const { isAuthenticated, isLoading, isInitialized } = useAuth();

  return {
    isAuthenticated,
    isLoading: isLoading || !isInitialized,
    isReady: isInitialized && !isLoading,
  };
};

/**
 * Hook para obtener datos del usuario
 */
export const useUser = () => {
  const { user } = useAuth();

  return {
    user,
    updateProfile: async () => ({ success: true }),
    fullName: user ? `${user.first_name || ''} ${user.last_name || ''}`.trim() || 'Usuario' : 'Usuario',
    initials: user ? `${user.first_name?.[0] || ''}${user.last_name?.[0] || ''}` : 'U',
  };
};

/**
 * Hook para acciones de autenticaci贸n
 */
export const useAuthActions = () => {
  const { login, register, logout, changePassword } = useAuth();

  return {
    login,
    register,
    logout,
    changePassword,
  };
};

export default AuthContext;
